var inProgress = false;
var paused = false;
var resumed = false;
function pause() {
  paused = true;
}
function resume() {
  paused = false;
  resumed = true;
  inProgress = false;
  work_hard();
}
function clear() {
  document.getElementById("scene").innerHTML="";
}
var savedObj = {
  i : -1, // sets
  i : -1, // where were we
  state : "n/a"
}
async function count_down(MSG, LIMIT) {
  for (var j = 0; j != LIMIT; ++j) {
    if (paused) {
    savedObj.j = j;
    savedObj.state = (MSG.substring(0, 4) == "WORK") ? "WORK": "REST";
throw 42;
}
    if (resumed) {
      j = savedObj.j;
      resumed = false; // state restored
    }
    clear();
    var ACT=LIMIT - j;
    if (ACT < 10) {
      ACT="0" + ACT;
    }
    banner(MSG + "<br/>" + ACT);
    if (LIMIT -j < 4) {
      beep();
    }
    await sleep(1000);
  }
}
function banner(msg) {
  document.getElementById("scene").innerHTML += "<h1>" + msg + "</h1>";
}
function bye() {
  clear();
  alert_end();
  banner("Well done");
  document.getElementById("congrats").style.display="block";
  $("#congrats").show().delay(3000).fadeOut();
}
async function rest(i, REST_INTERVAL) {
  await count_down("REST " +i, REST_INTERVAL);
}
function alert_start() {}
function alert_end() {
  beep();
}
function showControls(enabled) {
  if (enabled) {
    $('#play').show();
    $('#pause').show();
    $('#stop').show();
    return;
  }
  $('#play').hide();
  $('#pause').hide();
  $('#stop').hide();
}
async function work_hard() {
  if (inProgress) {
    console.log("already started");
    return;
  }
  showControls(true);
  var NSET = document.getElementById("NSET").value;
  var WORK_INTERVAL = document.getElementById("WORK_INTERVAL").value;
  var REST_INTERVAL = document.getElementById("REST_INTERVAL").value;
  inProgress = true;
  for (var i=1; i <= NSET; ++i) {
    if (resumed) {
      i = savedObj.i;
    }
try{
    if (resumed && savedObj.state == "WORK") {
      await count_down("WORK " + i, WORK_INTERVAL);
    }
    else if (!resumed) {
      alert_start();
      await count_down("WORK " + i, WORK_INTERVAL);
      alert_end();
    }
    if (i <= NSET-1) {
      await rest(i, REST_INTERVAL);
    }
  
} catch(e) {
  savedObj.i = i;
  throw e;
}
}
  bye();
  showControls(false);
  inProgress = false;
}
function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}
